package day1;

public class Swap2Numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		int b= 20;
		int temp;
	
		
		System.out.println("Before Swapping a= "+a+" & b= "+b);
		
		temp=a;
		a=b;
		b=temp;
		
		
		System.out.println("After Swapping a= "+a+" & b= "+b);

	}

}
